  `id` varchar(32) NOT NULL,
  `fk_office_id` varchar(64) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `customer_id` varchar(32) DEFAULT NULL COMMENT '外键评价人编号',
  `customer_name` varchar(20) DEFAULT NULL COMMENT '评价人姓名',
  `weibo_id` varchar(32) DEFAULT NULL,
  `content` text COMMENT '原文',
  `page_url` varchar(255) DEFAULT NULL,
  `comment_time` datetime DEFAULT NULL COMMENT '评价时间',
  `source_host_name` varchar(10) DEFAULT NULL,
  `source_host` varchar(30) DEFAULT NULL COMMENT '来源',
  `crisis_level` int(11) DEFAULT NULL COMMENT '危机级别',
