  `create_by` varchar(32) DEFAULT NULL COMMENT '创建人',
  `update_date` datetime DEFAULT NULL COMMENT '修改时间',
  `update_by` varchar(32) DEFAULT NULL COMMENT '修改人',
  `del_flag` char(1) DEFAULT '0' COMMENT '删除标记',
  `auto_id` int(11) NOT NULL AUTO_INCREMENT,
  `remarks` text,
  PRIMARY KEY (`id`),
  UNIQUE KEY `auto_id_2` (`auto_id`),
  UNIQUE KEY `weiboId` (`weibo_id`),
  UNIQUE KEY `weibo_id` (`weibo_id`),
  KEY `部门` (`fk_office_id`),
  KEY `auto_id` (`auto_id`),
  CONSTRAINT `部门` FOREIGN KEY (`fk_office_id`) REFERENCES `sys_office` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=216 DEFAULT CHARSET=utf8 COMMENT='危机数据表';

